/// This library contains character-code definitions.
library path.characters;

const PLUS = 0x2b;
const MINUS = 0x2d;
const PERIOD = 0x2e;
const SLASH = 0x2f;
const ZERO = 0x30;
const NINE = 0x39;
const COLON = 0x3a;
const UPPER_A = 0x41;
const UPPER_Z = 0x5a;
const LOWER_A = 0x61;
const LOWER_Z = 0x7a;
const BACKSLASH = 0x5c;
